import React, {useState} from 'react';
import "../../assets/main.css";

/**
 * Chat holds the layout of all messages and the input/sent
 * 
 * @returns a div containing all messages aswell as input for new messages
 */
const Chat = () => {

    //All old messages
    const [messages, setMessages] = useState(["Hej", "Då"]);
    //When typing a new message
    const [newMessage, setNewMessage] = useState(''); 

    const sendMessage = (e) =>{
        e.preventDefault(); 
        setMessages(prev => [...prev, newMessage]); 
        setNewMessage('');
        };


    return (
        <div className ="bg-gray-800 h-screen" >
            <div id="messages">
                {messages.map((message, index) => (
                    <div key ={index} className="static bg-gray-400 display-flex">           
                        {message}
                    </div>            
                ))}
            </div>
            <div id="input&send"className="absolute inset-x-0 bottom-0 h-10 ">
                <form onSubmit={sendMessage}>
                    <input className="bg-blue-200 w-3/4 h-full"type="text" onChange = {e => setNewMessage(e.target.value)} placeholder="New message..."/>
                    <button onClick={sendMessage} className="h-full w-1/4 bg-blue-100"type="submit"> 
                    Send
                    </button>
                </form>
            </div>
        </div>
    )
}


export default Chat;
