import React from 'react';

const ThreadInfo = ({ thread, handleFocusedThread }) => {
    console.log(thread);
    const handle = (e)=>{
        console.log(e.target.id);
        handleFocusedThread(e);
    };
    return (
        <div id ={thread.id} onClick ={handle} className="rounded border-2 border-solid border-black w-full p-2 mt-2 flex flex-col shadow-inner shadow-lg bg-gray-700">
        <div className="flex flex-row justify-between">
          <div className=" text-3xl text-white pl-2 w-auto">
            {thread.rootPost.rootHeader}
          </div>
          <div className="text-xs text-white">
            Posted by: {thread.username} on {thread.timestamp}
          </div>
        </div>

      </div>
    )
}

export default ThreadInfo;
