import React, {useState} from 'react';
import "../../assets/main.css";

/**
 * LandingPage holds the layout design grid of the app.
 * 
 * @returns a div containing the SideDisplayList, InfoDisplayList and FocusedView components 
 */
const Chat = () => {

    //All old messages
    const [messages, setMessages] = useState(["Hej", "Då"]);
    //When typing a new message
    const [newMessage, setNewMessage] = useState(''); 

    return (
        <div className ="bg-gray-800 container mx-auto h-screen" >
            <div id="messages">
                {messages.map((message, index) => (
                    <div key ={index} className="static bg-gray-400 display-flex">           
                        {message}
                    </div>            
                ))}
            </div>
            <div id="input&send"className="static w-full">
                <input className="bg-blue-200 w-5/6"type="text" onChange = {e => setNewMessage(e.target.value)} placeholder="New message..."/>
                <button onClick={e => {e.preventDefault(); setMessages(prev => [...prev, newMessage]); setNewMessage('');}} className="w-1/6 bg-blue-100"type="submit"> 
                Send
                </button>
            </div>
        </div>
    )
}


export default Chat;
