import React, { useState } from "react";

const CreateThread = ({ setShowCreateThread }) => {
  const [summary, setSummary] = useState("");
  const [details, setDetails] = useState("");

  return (
    <div className="addFriend-custom-overlay">
      <div className="addFriend-custom-modal">
        <div className="addFriend-custom-modal-body">
          <label htmlFor="AddFriend" className="text-base">
            {" "}
            Enter the header for your thread
          </label>
          <input
            id="Summary"
            placeholder="Summary..."
            onChange={(e) => {
              setSummary(e.target.value);
            }}
            className=" flex flex-wrap shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            type="text"
          ></input>
          <h1>Enter a detailed description:</h1>

          <textarea
            onChange={(e) => {
              setDetails(e.target.value);
            }}
            placeholder="Details.."
            type="text"
            className="mt-2 mb-2 h-20 flex flex-warp shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          ></textarea>
          {/* On add, do a friend request and update success state accordingly */}
          <button className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 border border-green-700 rounded">
            Create
          </button>
          <button
            className="bg-red-900 hover:bg-red-700 text-white font-bold py-2 px-4 border border-red-700 rounded"
            onClick={() => {
              setShowCreateThread(false);
            }}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateThread;
