import React, {useState} from 'react';

const StartChat = ({friends}) => {


    const [cusen, setCusen] = useState([]);
    const [chosen, setChosen] = useState(); 

    const handleCheck = (e) =>{
        if(cusen == [] && e.target.checked){
            setChosen(e.target.value);
            setCusen(chosen);
        }
        if (e.target.checked){
            setChosen(e.target.value);
            setCusen(prev => [...prev, chosen]);
        }
        else{
            const currentlyChosen = cusen.filter(friend => e.target.value != friend);
            setCusen(currentlyChosen);
        }
    };

    return (
        <div className=" z-40 bg-white rounded">
            Choose friends to add:
            {friends.map((friend, index) => {

                return <div className="flex flex-row justify-between">
                             <div className="">{friend}</div>
                             <input onChange ={handleCheck} type="checkbox" value ={friend}/>
                        </div>
            })} 
        </div>
    )
}

export default StartChat;
