import React, {useState} from 'react';


const Chat = () => {

    //All old messages
    const [messages, setMessages] = useState(["Hej", "Då"]);
    //When typing a new message
    const [newMessage, setNewMessage] = useState(''); 

    return (
        <div className ="bg-gray-200">
            {messages.map((message, index) => (
                <div key ={index} className="bg-gray-400 display-flex size-">           
                    {message}
                </div>            
            ))}
            <div>
                <input type="text" onChange = {e => setNewMessage(e.target.value)} placeholder="New message..."/>
                <button type="submit"/> 

            </div>
        </div>
    )
}


export default Chat;
